import React, { useState, useEffect } from "react";
import Axios from "axios";

const ModifyApy = () => {
  const [stableApy, setStableApy] = useState(5);
  const [big4Apy, setBig4Apy] = useState(0);
  const [bscApy, setBscApy] = useState(0);

  let apy = [
    { name: "Stable", value: stableApy },
    { name: "Big 4", value: big4Apy },
    { name: "BSC", value: bscApy },
  ];

  //get all apys from db and set them to state
  const getApy = () => {
    Axios.get("/apy/getAll")
      .then((res) => {
        console.log(res.data);
        res.data.map((data) => {
          switch (data.name) {
            case "stable":
              setStableApy(data.value);
              break;
            case "big4":
              setBig4Apy(data.value);
              break;
            case "bsc":
              setBscApy(data.value);
              break;
            default:
              break;
          }
        });
      })
      .catch((err) => {
        console.log(err);
      });
  };
  useEffect(() => {
    getApy();
  }, []);

  const saveApy = () => {
    Axios.post("/apy/update", {
      stableApy: stableApy,
        big4Apy: big4Apy,
        bscApy: bscApy,
    })
      .then((res) => {
        console.log(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  console.log(stableApy);

  return (
    <>
      <h1>ModifyApy</h1>
      <div className="d-flex flex-column">
        <label>STABLE</label>
        <input type="number" onChange={(e) => setStableApy(e.target.value)} />
        <label>BIG4</label>
        <input type="number" onChange={(e) => setBig4Apy(e.target.value)} />
        <label>BSC</label>
        <input type="number" onChange={(e) => setBscApy(e.target.value)} />
        <button onClick={saveApy}>Modificar</button>
      </div>
    </>
  );
};

export default ModifyApy;
