import React, {useState, useEffect} from 'react'
import Axios from 'axios'

const ModifyApy = () => {
    const [stableApy, setStableApy] = useState(0);
    const [big4Apy, setBig4Apy] = useState(0);
    const [bscApy, setBscApy] = useState(0);

//get all apys from db and set them to state
    const getApy = () => {
        Axios.get('/apy/getAll')
        .then(res => {
            console.log(res.data);
            
        })
        .catch(err => {
            console.log(err);
        })
    }
    useEffect(() => {
        getApy();
    }, []);


  return (
      <>
      <h1>ModifyApy</h1>
      <div className="d-flex flex-column">
      <label>STABLE</label>
      <input type="number" onChange={(e) => setStableApy(e.target.value)}/>
      <label>BIG4</label>
      <input type="number" onChange={(e) => setBig4Apy(e.target.value)}/>
      <label>BSC</label>
      <input type="number" onChange={(e) => setBscApy(e.target.value)}/>
      <button >Modificar</button>
      </div>
      </>
    
  )
}

export default ModifyApy
