import React, { Component, useState } from 'react'
import './Login.css'
import Header from '../../layout/Header';
import FixedBottomNavigation from '../../layout/Footer';
import { useHistory } from "react-router-dom";
import axios from "axios";
import UserContext from "../../../context/userContext";
import ErrorNotice from "../../components/misc/ErrorNotice";


const Login = () => {

    //LOGIN STATES
    const [emailLogin, setEmailLogin] = useState();
    const [passwordLogin, setPasswordLogin] = useState();
    const [error, setError] = useState();

    const { setUserData } = useContext(UserContext);
    const history = useHistory();

    const submit = async (e) => {
        e.preventDefault();
        try{
            const loginUser = {emailLogin, passwordLogin};
            const loginResponse = await axios.post("http://localhost:5000/users/login", loginUser);
            setUserData({
                token: loginResponse.data.token,
                user: loginResponse.data.user
            });
            localStorage.setItem("auth-token", loginResponse.data.token);
            localStorage.setItem("isAuthenticated", "true");
            history.push("/");
        } catch(err) {
            err.response.data.msg && setError(err.response.data.msg)
        }
        
    };

    //REGISTER STATES
    const [email, setEmail] = useState();
    const [password, setPassword] = useState();
    const [passwordCheck, setPasswordCheck] = useState();
    const [displayName, setDisplayName] = useState();
    const saldo = 0;

    

    const submitRegister = async (e) => {
        e.preventDefault();

        try{
            const newUser = {email, password, passwordCheck, displayName, saldo};
            await axios.post("http://localhost:5000/users/register", newUser);
            const loginResponse = await axios.post("http://localhost:5000/users/login", {
                email, password
            });
            setUserData({
                token: loginResponse.data.token,
                user: loginResponse.data.user
            });
            localStorage.setItem("auth-token", loginResponse.data.token);
            history.push("/");
        } catch(err) {
            err.response.data.msg && setError(err.response.data.msg)
        }
        
    };
 


    return (
        <>
        <Header />
        <div class="section">
            <div class="container">
                <div class="row full-height justify-content-center">
                    <div class="col-12 text-center align-self-center ">
                        <div class="section pb-5 pt-5 pt-sm-2 text-center">
                            <h6 class="mb-0 pb-3"><span>Log In </span><span>Sign Up</span></h6>
                            <input class="checkbox" type="checkbox" id="reg-log" name="reg-log" />
                            <label for="reg-log"></label>
                            <div class="card-3d-wrap mx-auto">
                                <div class="card-3d-wrapper">
                                    <div class="card-front">
                                        <div class="center-wrap">
                                            <div class="section text-center">
                                                <h4 class="mb-4 pb-3">Log In</h4>
                                                {error && <ErrorNotice message={error} clearError={() => setError(undefined)} />}
                                                <form onSubmit={submit}>
                                                <div class="form-group">
                                                    <input type="email" name="logemail" class="form-style" placeholder="Your Email" id="email" onChange={e => setEmail(e.target.value)}autocomplete="off" />
                                                    <i class="input-icon uil uil-at"></i>
                                                </div>
                                                <div class="form-group mt-2">
                                                    <input type="password" name="logpass" class="form-style" placeholder="Your Password" id="password" onChange={e => setPassword(e.target.value)} autocomplete="off" />
                                                    <i class="input-icon uil uil-lock-alt"></i>
                                                </div>
                                                <a href="#"  type="submit" value="Login" class="btn mt-4">submit</a>
                                                <p class="mb-0 mt-4 text-center"><a href="#0" class="link">Forgot your password?</a></p>
                                                </form>
                                            </div>
                                        </div>
                                    </div>
                                    <div class="card-back">
                                        <div class="center-wrap">
                                            <div class="section text-center">
                                                <h4 class="mb-4 pb-3">Sign Up</h4>
                                                {error && <ErrorNotice message={error} clearError={() => setError(undefined)} />}

                                                <div class="form-group">
                                                    <input type="text" id="dsplay-name" onChange={e => setDisplayName(e.target.value)} />
                                                    <i class="input-icon uil uil-user"></i>
                                                </div>
                                                <div class="form-group mt-2">
                                                    <input  type="email" id="email" onChange={e => setEmail(e.target.value)} />
                                                    <i class="input-icon uil uil-at"></i>
                                                </div>
                                                <div class="form-group mt-2">
                                                    <input type="password" id="password" onChange={e => setPassword(e.target.value)} />
                                                    <i class="input-icon uil uil-lock-alt"></i>
                                                </div>
                                                <div class="form-group mt-2">
                                                    <input type="password" placeholder="Confirm password" onChange={e => setPasswordCheck(e.target.value)} />
                                                    <i class="input-icon uil uil-lock-alt"></i>
                                                </div>
                                                <a href="#" class="btn mt-4">submit</a>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
        <FixedBottomNavigation/>
        </>
    )
}

export default Login
