/* eslint-disable react-hooks/exhaustive-deps */
import * as React from 'react';
import { Link } from 'react-router-dom';
// Logo Perception
import logo from '../../../assets/Parado.svg'
// Componentes para el menu lateral
import MuiDrawer from '@mui/material/Drawer';
import MuiAppBar from '@mui/material/AppBar';
import CssBaseline from '@mui/material/CssBaseline';
import { styled, useTheme } from '@mui/material/styles';
import { Button, IconButton, Divider, List, ListItem, ListItemText, Box, ListItemIcon, Toolbar } from '@mui/material';
// Icons
import CollectionsIcon from '@mui/icons-material/Collections';
import AutoGraphIcon from '@mui/icons-material/AutoGraph';
import CollectionsBookmarkIcon from '@mui/icons-material/CollectionsBookmark';
import DashboardIcon from '@mui/icons-material/Dashboard';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
// Segun los roles se renderizan distintas opciones en el menu
import { useUser } from '../../context/UserContext'

const drawerWidth = 240;

const openedMixin = (theme) => ({
    width: drawerWidth,
    transition: theme.transitions.create('width', {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.enteringScreen,
    }),
    overflowX: 'hidden',
});

const closedMixin = (theme) => ({
    transition: theme.transitions.create('width', {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.leavingScreen,
    }),
    overflowX: 'hidden',
    width: `calc(${theme.spacing(5)} + 1px)`,
    [theme.breakpoints.up('sm')]: {
        width: `calc(${theme.spacing(6)} + 1px)`,
    },
});

const DrawerHeader = styled('div')(({ theme }) => ({
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-end',
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
}));

const Drawer = styled(MuiDrawer, { shouldForwardProp: (prop) => prop !== 'open' })(
    ({ theme, open }) => ({
        width: drawerWidth,
        flexShrink: 0,
        whiteSpace: 'nowrap',
        boxSizing: 'border-box',
        ...(open && {
            ...openedMixin(theme),
            '& .MuiDrawer-paper': openedMixin(theme),
        }),
        ...(!open && {
            ...closedMixin(theme),
            '& .MuiDrawer-paper': closedMixin(theme),
        }),
    }),
);

const AppBar = styled(MuiAppBar, {
    shouldForwardProp: (prop) => prop !== 'open',
})(({ theme, open }) => ({
    zIndex: theme.zIndex.drawer + 1,
    transition: theme.transitions.create(['width', 'margin'], {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.leavingScreen,
    }),
    ...(open && {
        marginLeft: drawerWidth,
        width: `calc(100% - ${drawerWidth}px)`,
        transition: theme.transitions.create(['width', 'margin'], {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.enteringScreen,
        }),
    }),
}));

const DrawerLayout = ({ children }) => {
   
    const [open, setOpen] = React.useState(true);

    const handleDrawerOpen = () => {
        setOpen(true);
    };
    const handleDrawerClose = () => {
        setOpen(false);
    };

    return (
        <Box sx={{ display: 'flex' }}>
            <CssBaseline />
            <AppBar position="fixed" open={open} sx={{ backgroundColor: '#28293D' }}>
                <Toolbar className="d-flex align-items-center justify-content-between">
                    <div className="d-flex align-items-center">
                        <IconButton
                            color="inherit"
                            aria-label="open drawer"
                            onClick={handleDrawerOpen}
                            edge="start"
                            sx={{
                                marginRight: 5,
                                ...(open && { display: 'none' }),
                            }}
                        >
                            <ChevronRightIcon />
                        </IconButton>
                        <Link to="/">
                            <img src={logo} alt="logo" />
                        </Link>
                    </div>
                    <Button color="info" sx={{ height: "39px", marginRight: "5rem" }} startIcon={<AccountCircleIcon />}>{currentAccount.slice(0, 5)}...{currentAccount.slice(37)} </Button>
                </Toolbar>
            </AppBar>
            <Drawer variant="permanent" open={open} sx={{ [`& .MuiDrawer-paper`]: { backgroundColor: '#28293D' }, }}>
                <DrawerHeader>
                    <IconButton onClick={handleDrawerClose} sx={{ color: '#FFFFFF' }}>
                        {theme.direction === 'rtl' ? <ChevronRightIcon /> : <ChevronLeftIcon />}
                    </IconButton>
                </DrawerHeader>
                {/* Primera lista */}
                <Divider sx={{ color: '#FFFFFF' }} />
                {role !== 'norole' &&
                    <List sx={{ color: '#FFFFFF' }}>
                        {role !== 'Artist' &&
                            <Link to="panel/categories">
                                <ListItem button>
                                    <ListItemIcon sx={{ color: '#FFFFFF' }}>
                                        <CollectionsBookmarkIcon />
                                    </ListItemIcon>
                                    <ListItemText primary={"Categorías NFT"} />
                                </ListItem>
                            </Link>}

                        {role === 'Artist' && <Link to="panel/gallery">
                            <ListItem button>
                                <ListItemIcon sx={{ color: '#FFFFFF' }}>
                                    <CollectionsIcon />
                                </ListItemIcon>
                                <ListItemText primary={"Galería"} />
                            </ListItem>
                        </Link>}

                        {role !== 'Artist' &&
                            <Link to="panel/ico">
                                <ListItem button>
                                    <ListItemIcon sx={{ color: '#FFFFFF' }}>
                                        <DashboardIcon />
                                    </ListItemIcon>
                                    <ListItemText primary={"ICO"} />
                                </ListItem>
                            </Link>}
                    </List>}
                {/* Segunda lista */}
                {role !== 'norole' && role !== 'Artist' &&
                    <>
                        <Divider sx={{ color: '#FFFFFF' }} />
                        <List sx={{ color: '#FFFFFF' }}>

                            <Link to="panel/categories">
                                <ListItem button>
                                    <ListItemIcon sx={{ color: '#FFFFFF' }}>
                                        <AutoGraphIcon />
                                    </ListItemIcon>
                                    <ListItemText primary={"Artistas"} />
                                </ListItem>
                            </Link>

                            {role === 'Owner' &&
                                <Link to="panel/administradores">
                                    <ListItem button>
                                        <ListItemIcon sx={{ color: '#FFFFFF' }}>
                                            <AutoGraphIcon />
                                        </ListItemIcon>
                                        <ListItemText primary={"Administradores"} />
                                    </ListItem>
                                </Link>}
                        </List></>}
            </Drawer>
            <Box component="main" sx={{ flexGrow: 1, p: 3, minHeight: '85.1vh' }}>
                <DrawerHeader />
                {children}
            </Box>
        </Box>
    )
}

export default DrawerLayout
