import React, { useState, useEffect } from 'react'
import { useParams, Link } from 'react-router-dom'
import Axios from 'axios';

const EditData = () => {
  const { id } = useParams();
  const [user, setUser] = useState([]);
  const [saldo, setSaldo] = useState();

  function traerUser (user){
    Axios.get(`/users/api/edit/${id}`)
            .then(res => {
              setUser(user => res.data)
            })
            .catch(err => {
              console.log(err)
            })
  }
  useEffect(() => {
    traerUser();
  }, []);

  const saveUser = () => {
    Axios.post(`/users/api/edit/${id}`, {
      saldo: saldo,
      userId : user._id
    })
      .then(res => {
        console.log(res)
        console.log(saldo)
      }
      ).catch(err => {
        console.log(err)
      })
  }

  console.log(user);

  return (
    <>
      <h1>Editar usuario</h1>
      <form>
      <label>Nombre del usuario: </label>
      <input type="text" defaultValue={user.displayName}/>
      <br/>
      <label>Email: </label>
      <input type="text" defaultValue={user?.email}/>
      <br/>
      <label>Saldo disponible: </label>
      <input type="text" defaultValue={user.saldo} onChange={e => setSaldo(e.target.value)}/>
      <br/>
      <label>ID del usuario: </label>
      <input type="text" defaultValue={user._id}/>
      {user?.vaults && user.vaults.map(vault => (
        <div key={vault._id}>
          <label>ID del vault: </label>
          <label type="text" >{vault._id}</label>
          <br/>
        </div>
      ))}
      <TableContainer component={Paper}>
      <Table sx={{ minWidth: 650 }} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell>Dessert (100g serving)</TableCell>
            <TableCell align="right">Calories</TableCell>
            <TableCell align="right">Fat&nbsp;(g)</TableCell>
            <TableCell align="right">Carbs&nbsp;(g)</TableCell>
            <TableCell align="right">Protein&nbsp;(g)</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {user?.vaults && user.vaults.map((vault) => (
            <TableRow
              key={vault._id}
              sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
            >
              <TableCell component="th" scope="row">
                {vault._id}
              </TableCell>
              <TableCell align="right">{vault._id}</TableCell>
              <TableCell align="right">{vault._id}</TableCell>
              <TableCell align="right">{vault._id}</TableCell>
              <TableCell align="right">{vault._id}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>

      <label >
        </label>
        <br/>
      <button onClick={saveUser}>Editar</button>
      </form>

    </>
  )
}

export default EditData
