import React, { useState, useEffect, useContext } from 'react'
import { useParams, Link } from 'react-router-dom'
import { VaultContext } from "../../../context/vaultContext";
import Axios from 'axios';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Button from '@mui/material/Button';

const EditData = () => {
  const { id } = useParams();
  const [user, setUser] = useState([]);
  const [saldo, setSaldo] = useState();
  const { endDate, setEndDate } = useContext(VaultContext);
  const [vault, setVault] = useState();

  var today = new Date();
  var final = new Date(new Date().setDate(today.getDate() + 90));

  let finalTimestamp = toTimestamp(final);
  console.log(finalTimestamp)

  function toTimestamp(strDate){
    var datum = Date.parse(strDate);
    return datum/1000;
 }

  function traerUser (user){
    Axios.get(`/users/api/edit/${id}`)
            .then(res => {
              setUser(user => res.data)
            })
            .catch(err => {
              console.log(err)
            })
  }

  useEffect(() => {
    traerUser();
    setEndDate(finalTimestamp)
  }, []);

  const saveUser = () => {
    Axios.post(`/users/api/edit/${id}`, {
      saldo: saldo,
      userId : user._id,
      
    })
      .then(res => {
        console.log(res)
        console.log(saldo)
      }
      ).catch(err => {
        console.log(err)
      })
  }

  //traer vaults 
  const traerVault = async (vault) => {
    
    await Axios.get(`/users/api/vaults/${id}/${vault}`)
      .then((res) => {
        res.data.vaults.map((vault) => {
          if(vault._id === id){
            setVault(vault)
            console.log(vault)
          }
        })
      })
      .catch((err) => {
        console.log(err);
      });
  }

  
  

  const activateVault = (vault) => {
    
    Axios.post(`/users/api/activate/${id}/${vault}`, {
      
      userId: user._id,
      vaultId: vault,
      
    })
      .then(res => {
        console.log(res)
      })
      .catch(err => {
        console.log(err)
      })
      window.location.reload();
  }

  console.log(user);

  return (
    <>
      <h1>Editar usuario</h1>
      <form>
      <label>Nombre del usuario: </label>
      <input type="text" defaultValue={user.displayName}/>
      <br/>
      <label>Email: </label>
      <input type="text" defaultValue={user?.email}/>
      <br/>
      <label>Saldo disponible: </label>
      <input type="text" defaultValue={user.saldo} onChange={e => setSaldo(e.target.value)}/>
      <br/>
      <label>ID del usuario: </label>
      <input type="text" defaultValue={user._id}/>
      <br/>
      <button onClick={saveUser}>Editar datos</button>
        <TableContainer >
        <Table sx={{ minWidth: 650 }} aria-label="simple table">
          <TableHead>
            <TableRow>
            <TableCell >ID</TableCell>
              <TableCell>Vault</TableCell>
              <TableCell align="right">Timelock</TableCell>
              <TableCell align="right">Invertido</TableCell>
              <TableCell align="right">Auto Renovar</TableCell>
              <TableCell align="right">Estado</TableCell>
              <TableCell align="right">Actions</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {user?.vaults && user.vaults.map((vault, index) => (
              <TableRow
                key={index}
                sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
              >
                <TableCell align="right">{vault._id}</TableCell>
                <TableCell component="th" scope="row">
                  {vault.vault}
                </TableCell>
                <TableCell align="right">{vault.timelock}</TableCell>
                <TableCell align="right">{vault.funds}</TableCell>
                <TableCell align="right">{(vault.autoRenew) ? 'activado' : 'no activado'}</TableCell>
                <TableCell align="right">{(vault.activated) ? 'activa' : ' no activa'}</TableCell>
                {(!vault.activated) ? <TableCell align="right"><Button variant="contained" onClick={() => traerVault(vault?._id)}>Aceptar y activar</Button></TableCell> : null}
                <TableCell align="right"><Link to={`/editvault/${id}/${vault._id}`}>Editar</Link></TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    
      </form>

    </>
  )
}

export default EditData
