import React, {useState, useEffect, useContext} from 'react'
import axios from 'axios'
import UserContext from '../../../context/userContext';


const VaultsSum = () => {
    const [cryptos, setCryptos] = useState([])
    const { userData, setUserData } = useContext(UserContext);


  
    const getSumOfFundsInUSD = () => {
        axios.get('https://min-api.cryptocompare.com/data/pricemulti?fsyms=BTC,ETC,ADA,BNB,Cake,LAND,BSW,&tsyms=USD')
        .then(res=> {
          const cryptos = res.data;
          setCryptos(cryptos);
        })
      }
    
      useEffect(() => {
        getSumOfFundsInUSD();
        
      }, [])

console.log(cryptos);
console.log(userData)

  return (
    <>
        <div className="cryptos">
            {Object.keys(cryptos).map((key) =>(
                <div key={key}>
                    <span >{key}</span>
                    <span >&nbsp;{cryptos[key].USD}</span>
                </div>
            ))}
        </div>
        <div className="vaultsSUM">
         
            {userData.user?.vaults.map((vault, index) => (
                <div key={index}>
                    <span >fondos : {vault.funds}</span>
                    {vault?.coins.map((coin, index) => (
                        <div key={index}>
                            <span >{coin.coin}</span>
                            <span >&nbsp;{coin.quantity}</span>
                            {/* compare coin.coin with cryptos and check if the same value */}
                            <h1>{cryptos[coin.coin].USD}</h1>

                            </div>
                    ))}
                    </div>
            ))}
        </div>

    </>
  )
}

export default VaultsSum
